import React, { Component } from "react";
import axios from "axios";


class Colors extends Component {
    state = {
        name: '',
        colors: [],
        loading: true
    }
    async componentDidMount() {
        const res = await axios.get('http://localhost:8000/api/colors');
        console.log(res.data.status);
        if (res.data.status === 200) {
            this.setState({
                colors: res.data.colors,
                loading: false
            });
        }
    }
    deleteColor = async (e, id) => {
        const thidClicked = e.currentTarget;
        const res = await axios.delete(`http://localhost:8000/api/delete-color/${id}`);
        if (res.data.status === 200) {
            thidClicked.closest("tr").remove();
            console.log(res.data.message);
        }
    }

    handleInput = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }


    render() {
        var products_HTMLTable = "";
        if (this.state.loading) {

            products_HTMLTable =
                <tr>
                    <td>loading...</td>
                    <td>loading...</td>
                    <td>loading...</td>

                </tr>
        } else {
            products_HTMLTable =
                this.state.colors.map((item) => {
                    return (
                        <tr key={item.id}>
                            <td >{item.id}</td>
                            <td>{item.name}</td>
                            <td>
                                <a className="btn btn-success btn-sm" href={`edit-product/${item.id}`}>Edit</a>
                                <a className="btn btn-danger btn-sm" onClick={(e)=> this.deleteColor(e,item.id)}> | Delete</a>
                            </td>
                        </tr>
                    );
                });
        }
        return (

            <div className="container">
                <div className="container">
                    <div className="row">
                        <div className="col-md-12">
                            <div className="card">
                                <div className="card-header" style={{ display: "flex", justifyContent: "space-between" }}>
                                    <h4>
                                        Colors

                                    </h4>
                                    <a className="btn btn-primary" href="/AddColor">Add Color</a>
                                </div>
                                <div className="card-body">
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th scope="col">#ID</th>
                                                <th scope="col">Name</th>

                                                <th scope="col">Action</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {products_HTMLTable}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

            </div>
        );
    }
}

export default Colors;
